//轮播图
public class SlideShowAdapter extends StaticPagerAdapter {
    private int[] images = {R.mipmap.banner,R.mipmap.banner};

    @Override
    public View getView(ViewGroup container, int position) {
        ImageView view = new ImageView(container.getContext());

//        view.setImageResource(images[position]);
//        Bitmap bitmap = Base64toBitUtil.stringToBitmap("");
        Bitmap bitmap = Base64toBitmap.stringtoBitmap("");
        view.setImageBitmap(bitmap);

        view.setScaleType(ImageView.ScaleType.CENTER_CROP);
        view.setLayoutParams(new ViewGroup.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT));
/*        String time = GetTimeStamp.getTime();
        String md5 = MD5Utils.getMD5(time);
        System.out.println("md5"+md5);*/
        return view;
    }

    @Override
    public int getCount() {
        return images.length;
    }



}
